import { Route, Routes, useParams } from "react-router-dom";
import Register from "./Registrtion";
import { Card, Row, Col, Button } from "react-bootstrap";

const ProductDetails = () => {
    const { productid } = useParams(); // Accessing the productid parameter from the URL
    console.log(productid);

    return (
        <section>
            <Register />
            <Routes className="justify-content-md-center" xs={2}>
                {/* Define routes for different product IDs */}
                <Route path="/productdetails/0">
                    {/* Render content for product with ID 0 */}
                    <p>Hello, Product 0</p>
                </Route>
                <Route path="/productdetails/1">
                    {/* Render content for product with ID 1 */}
                    <p>Hello, Product 1</p>
                </Route>
                {/* Add more routes for other product IDs as needed */}
            </Routes>
            {/* Example of a static Card component */}
            <Card className="mt-5" style={{ height: '18rem', background: '#ADD8E6' }}>
                <Card.Footer class='display-1'>
                    <p align='center'>The generics</p>
                </Card.Footer>
            </Card>
        </section>
    );
};

export default ProductDetails;
