import DataContext from "./auth-context";
import React, { useState, useContext} from "react";
const StoreContext=(props)=>{
    const [arr,setarr]= useState([        {
        
        title: 'Blue Color',
        
        price: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%204.png',
        quantity:0
        
        }])
    // const Ctx = DataContext.createContext()
    const additemFunc = (props, item) => {
        const existingItemIndex = arr.findIndex(ele => ele.imageUrl === props.imageUrl);
        if (existingItemIndex !== -1) {
            const newArray = [...arr];
            newArray[existingItemIndex].quantity++;
            setarr(newArray);
        } else {
            setarr([props, ...arr]);
        }
    }
    
    const j =[0,1,2,3]
    const removeItemFunc=(ele1,item1)=>{
        console.log(arr,'old')
        const newarr = arr.filter((ele,item)=>(
            ele1!==ele
        ))
        setarr(newarr)
        console.log(arr,item1)

    }
    const setarrfunc=(Arr)=>{
        setarr(Arr)
    }
    const StoreContext2 = {
        additem:additemFunc,
        removeitem:removeItemFunc,
        arr:arr,
        setarr:setarrfunc
    }
    return(
        <DataContext.Provider value={StoreContext2}>
            {props.children}
        </DataContext.Provider>
    )


}
export default StoreContext
