import DataContext from "./auth-context";
import React, { useState, useContext} from "react";
const StoreContext=(props)=>{
    const [arr,setarr]= useState([])
    // const Ctx = DataContext.createContext()
    const additemFunc=(props)=>{
        setarr([props,...arr])

    }
    const removeItemFunc=(item1)=>{
        console.log(arr,'old')
        const newarr = arr.filter((item)=>(
            item!==item1
        ))
        setarr(newarr)
        console.log(arr,item1)

    }
    const StoreContext2 = {
        additem:additemFunc,
        removeitem:removeItemFunc,
        arr:arr
    }
    return(
        <DataContext.Provider value={StoreContext2}>
            {props.children}
        </DataContext.Provider>
    )


}
export default StoreContext
