import { Button, Row, Col } from "react-bootstrap";
import classes from './Cart.module.css';
import { useState, useContext } from "react";
import axios from 'axios';
import DataContext from "../../Store/auth-context";

async function Cart() {
    const Ctx = useContext(DataContext);
    const [cartItems, setCartItems] = useState([]);

    async function fetchData() {
        try {
            const response = await axios.get('https://crudcrud.com/api/bfd3cbd42bcf4c948bebd4fbec5d4f84/ecom');
            setCartItems(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    async function handleDelete(item) {
        try {
            await axios.delete(`https://crudcrud.com/api/bfd3cbd42bcf4c948bebd4fbec5d4f84/ecom/${item._id}`);
            const updatedItems = cartItems.filter(cartItem => cartItem._id !== item._id);
            setCartItems(updatedItems);
        } catch (error) {
            console.error('Error deleting item:', error);
        }
    }

    // Fetch data when component mounts
    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div className={classes.modal}>
            <h1 align='center'>CART</h1>
            <Row>
                <Col>
                    <b>Image</b>
                    <hr />
                </Col>
                <Col>
                    <b style={{ left: "30%", position: 'relative' }}>Price</b>
                    <hr />
                </Col>
                <Col>
                    <b>Quantity</b>
                    <hr />
                </Col>
            </Row>
            {cartItems.map(item => (
                <Row key={item._id}>
                    <Col>
                        <img src={item.imageUrl} style={{ height: '90px', width: '100px' }} alt={item.title} />
                    </Col>
                    <Col>{item.price}</Col>
                    <Col>
                        {item.quantity}
                        <Button style={{ background: 'red' }} onClick={() => handleDelete(item)}>Remove</Button>
                    </Col>
                </Row>
            ))}
        </div>
    );
}

export default Cart;
