        setarr(...arr,props)

    }
    const removeItemFunc=(ele1,item1)=>{
        const newarr = arr.filter((ele,item)=>(
            ele1!==ele
        ))
        setarr(newarr)

    }
    const StoreContext = {
        additem:additemFunc,
        removeitem:removeItemFunc,
        arr:arr
    }
    return(
        <DataContext.Provider value={DataContext}>
            {props.children}
        </DataContext.Provider>
    )


}
export default StoreContext
