import {Routes,Route} from 'react-router-dom' 
import { lazy,Suspense } from 'react'
// import About from '../Components/pages/About'
const About=lazy(()=> import('../Components/pages/About'))
import Home from '../Components/pages/Home'
// import Contact from '../Components/pages/Contact'
const Contact=lazy(()=> import('../Components/pages/Contact'))
const Containerr = lazy(()=> import('../Components/Container'))
// import Containerr from '../Components/Container'
import ProductDetails from '../Components/ProductDetails'
import Login from '../Components/pages/Login'
import { useContext } from 'react'
import DataContext from '../Store/auth-context'
const MyRoutes=()=>{
    const Ctx = useContext(DataContext)
    console.log(Ctx)

    return(

        <Routes>
            
            <Route path='/' element ={<Home/>}/>
            { Ctx.islog && <Route path='/store' element ={<Suspense><Containerr/></Suspense>}/>}
            <Route path="/store/:productid" element={<ProductDetails />} />

            <Route path='/home' element ={<Home/>}/>
            <Route path='/contact' element ={<Suspense><Contact/></Suspense>}/>
            <Route path='/about' element ={<Suspense fallback={<p>Loading...</p>}><About/></Suspense>}/>
            <Route path='*' element ={<Home/>}/>
            <Route path='/login' element={<Login/>}/>
        </Routes>
    )
}
export default MyRoutes
