import { Button } from "react-bootstrap"
import { useState } from "react"
import Cart from "./Cart"
const CartButton=()=>{
    const [showcart,setshowcart]=useState(false)
   return(
    <>
        <Button onClick={()=>{setshowcart(!showcart)}}>
            Show-Cart
        </Button>
        {showcart && <Cart></Cart> }
    </>


   ) 
}
export default CartButton
