import DataContext from "./auth-context";
import React, { useState, useContext} from "react";
const StoreContext=(props)=>{
    const [arr,setarr]= useState([])
    // const Ctx = DataContext.createContext()
    const additemFunc=(props,item)=>{
        console.log('inside additem')
        const k = arr.map((ele,item1)=>{
            if(ele==props){
                arr[item].quantity++
                console.log('inside map if')
            }
            else{
                arr[item].quantity=1
                setarr([props,...arr])
                console.log('inside else map')
            }


        })
        setarr(k)
        
    }
    const j =[0,1,2,3]
    const removeItemFunc=(ele1,item1)=>{
        console.log(arr,'old')
        const newarr = arr.filter((ele,item)=>(
            ele1!==ele
        ))
        setarr(newarr)
        console.log(arr,item1)

    }
    const setarrfunc=(Arr)=>{
        setarr(Arr)
    }
    const StoreContext2 = {
        additem:additemFunc,
        removeitem:removeItemFunc,
        arr:arr,
        setarr:setarrfunc
    }
    return(
        <DataContext.Provider value={StoreContext2}>
            {props.children}
        </DataContext.Provider>
    )


}
export default StoreContext
